<?phpnamespace Api\Monasteries;use Phalcon\Mvc\Model;class PostLangs extends Model{    public $post_id, $title, $lang_id;    public function getSource()    {        return 'post_langs';    }    public function initialize()    {        $this->hasOne('post_id', Posts::class, 'id', ['alias' => 'post']);    }    /**     * @return mixed     */    public function getPostId()    {        return $this->post_id;    }    /**     * @param mixed $post_id     */    public function setPostId($post_id)    {        $this->post_id = $post_id;    }    /**     * @return mixed     */    public function getTitle()    {        return $this->title;    }    /**     * @param mixed $title     */    public function setTitle($title)    {        $this->title = $title;    }    /**     * @return mixed     */    public function getLangId()    {        return $this->lang_id;    }    /**     * @param mixed $lang_id     */    public function setLangId($lang_id)    {        $this->lang_id = $lang_id;    }}