<?phpnamespace Api\Monasteries;use Phalcon\Mvc\Model;class PostTags extends Model{    protected $id;    protected $post_id;    protected $tag_id;    public function getSource()    {        return 'post_tags';    }    public function initialize()    {        $this->belongsTo(            'post_id',            'Posts',            'id'        );        $this->belongsTo(            'tag_id',            'Tags',            'id'        );    }}