<?phpnamespace Api\Monasteries;use Phalcon\Mvc\Model;use Phalcon\Mvc\Model\Manager;class Posts extends Model{    public $id;    public $created_at;    public $post_type_id;    public function getSource()    {        return 'posts';    }    public function initialize()    {        $this->hasMany('id', PostLangs::class, 'post_id', ['alias' => 'postLang']);        $this->hasMany('id', Monasteries::class, 'post_id', ['alias' => 'monastery']);    }    /**     * @return mixed     */    public function getId()    {        return $this->id;    }    public function getPostTags()    {        return PostTags::find([            'columns' => 'tag_id',            'conditions' => 'post_id = :id:',            'bind' => [                'id' => $this->id            ]        ]);    }    public function getCurrentPost(int $lang = 1)    {        return PostLangs::findfirst([            'conditions' => 'post_id = :id: AND lang_id = :lang:',            'bind' => [                'id' => $this->id,                'lang' => $lang            ]        ]);    }    public function getTypes()    {        return PostTypes::findfirst([            'conditions' => 'id = :id:',            'bind' => [                'id' => $this->post_type_id,            ]        ]);    }    public function getMonastery()    {        return Monastery::findfirst([            'conditions' => 'monastery_id = :id:',            'bind' => [                'id' => $this->id,            ]        ]);    }}