<?phpnamespace Api\Monasteries;use Phalcon\Mvc\Model;class Tags extends Model{    public $id;    public function getSource()    {        return 'tags';    }    public function getCurrentTag()    {        return TagLangs::findFirst([            'conditions' => 'tag_id = :id: AND lang_id = :lang:',            'bind' => [                'id' => $this->id,                'lang' => 1            ]        ]);    }}